CREATE TABLE shersched.courtroom ( courtroom_id uuid NOT NULL, courtroom_cd varchar(32) NOT NULL, courtroom_name varchar(100) NOT NULL, location_id uuid NOT NULL, created_by varchar(32) NOT NULL, updated_by varchar(32) NOT NULL, created_dtm timestamptz NOT NULL, updated_dtm timestamptz NOT NULL, revision_count numeric(10) NOT NULL, CONSTRAINT pk_crtr PRIMARY KEY (courtroom_id), CONSTRAINT uk_locn_crtr_cd UNIQUE (location_id,courtroom_cd) );
